class mapContent extends Resources {
  data = allPlacesInFuto

  renderHTML() {
    let contentHTML = ``;
    
    this.data.forEach((d, i) => {
      // Create carousel items HTML
      let carouselItemsHTML = '';
      d.images.forEach((image, imgIndex) => {
        const isActive = imgIndex === 0 ? 'active' : '';
        carouselItemsHTML += `
          <div class="hidden duration-700 ease-in-out ${imgIndex === 0 ? '' : ''}" data-carousel-item="${isActive}">
            <img src="${image}" 
                 class="absolute block w-full h-full object-cover -translate-x-1/2 -translate-y-1/2 top-1/2 left-1/2" 
                 alt="${d.name} - Image ${imgIndex + 1}" 
                 loading="lazy">
          </div>`;
      });

      // Only show carousel controls if there are multiple images
      const showControls = d.images.length > 1;
      
      contentHTML += `     
        <div class="max-w-sm p-6 bg-white/75 border border-gray-200 rounded-lg shadow-sm dark:bg-green-800/15 dark:border-gray-700">
          <div id="gallery-${i}" class="relative w-full mb-4" data-carousel="slide">
            <!-- Carousel wrapper -->
            <div class="relative h-56 overflow-hidden rounded-lg md:h-96">
              ${carouselItemsHTML}
            </div>
            
            ${showControls ? `
            <!-- Slider indicators -->
            <div class="absolute z-30 flex -translate-x-1/2 bottom-5 left-1/2 space-x-3 rtl:space-x-reverse">
              ${d.images.map((_, idx) => `
                <button type="button" 
                        class="w-3 h-3 rounded-full ${idx === 0 ? 'bg-white' : 'bg-white/50 hover:bg-white'}" 
                        aria-current="${idx === 0 ? 'true' : 'false'}" 
                        aria-label="Slide ${idx + 1}" 
                        data-carousel-slide-to="${idx}">
                </button>
              `).join('')}
            </div>
            
            <!-- Slider controls -->
            <button type="button" 
                    class="absolute top-0 start-0 z-30 flex items-center justify-center h-full px-4 cursor-pointer group focus:outline-none" 
                    data-carousel-prev>
              <span class="inline-flex items-center justify-center w-10 h-10 rounded-full bg-white/30 dark:bg-gray-800/30 group-hover:bg-white/50 dark:group-hover:bg-gray-800/60 group-focus:ring-4 group-focus:ring-white dark:group-focus:ring-gray-800/70 group-focus:outline-none">
                <svg class="w-4 h-4 text-white dark:text-gray-800 rtl:rotate-180" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 6 10">
                  <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M5 1 1 5l4 4"/>
                </svg>
                <span class="sr-only">Previous</span>
              </span>
            </button>
            <button type="button" 
                    class="absolute top-0 end-0 z-30 flex items-center justify-center h-full px-4 cursor-pointer group focus:outline-none" 
                    data-carousel-next>
              <span class="inline-flex items-center justify-center w-10 h-10 rounded-full bg-white/30 dark:bg-gray-800/30 group-hover:bg-white/50 dark:group-hover:bg-gray-800/60 group-focus:ring-4 group-focus:ring-white dark:group-focus:ring-gray-800/70 group-focus:outline-none">
                <svg class="w-4 h-4 text-white dark:text-gray-800 rtl:rotate-180" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 6 10">
                  <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="m1 9 4-4-4-4"/>
                </svg>
                <span class="sr-only">Next</span>
              </span>
            </button>
            ` : ''}
          </div>

          <div>
            <a href="${d.mapLink}" target="_blank" rel="noopener noreferrer">
              <h5 class="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white hover:text-green-600 dark:hover:text-green-400 transition-colors">
                ${d.name}
              </h5>
            </a>
            <p class="mb-3 font-normal text-gray-700 dark:text-gray-400">
              ${d.description}
            </p>
            <a href="${d.mapLink}" 
               target="_blank" 
               rel="noopener noreferrer"
               class="inline-flex items-center px-3 py-2 text-sm font-medium text-center text-white bg-green-700 rounded-lg hover:bg-green-800 focus:ring-4 focus:outline-none focus:ring-green-300 dark:bg-green-600 dark:hover:bg-green-700 dark:focus:ring-green-800">
              <svg class="w-4 h-4 me-2" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 24 24">
                <path fill-rule="evenodd" d="M11.906 1.994a8.002 8.002 0 0 1 8.09 8.421 7.996 7.996 0 0 1-1.297 3.957.996.996 0 0 1-.133.204l-.108.129c-.178.243-.37.477-.573.699l-5.112 6.224a1 1 0 0 1-1.545 0L5.982 15.26l-.002-.002a18.146 18.146 0 0 1-.309-.38l-.133-.163a.999.999 0 0 1-.13-.202 7.995 7.995 0 0 1 6.498-12.518ZM15 9.997a3 3 0 1 1-5.999 0 3 3 0 0 1 5.999 0Z" clip-rule="evenodd"/>
              </svg>
              View on Map
              <svg class="rtl:rotate-180 w-3.5 h-3.5 ms-2" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 14 10">
                <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M1 5h12m0 0L9 1m4 4L9 9"/>
              </svg>
            </a>
          </div>
        </div>
      `;
    });

    return contentHTML;
  }
}


const mapData = new mapContent("map-data")

export { mapResources, timetable, armsOfGov, calendar, mapData };
