const timetablesData = new TimetableContent("timetable-data")

class CalendarData extends Resources{
  data = allActivities;

  semester() {
    const today = new Date();

    const semesters = Array.isArray(this.data) ? this.data : [];
    if (semesters.length === 0) return null;

    let closest = null; // { diff: Number, semester: String, activity: Object }

    semesters.forEach((sem) => {
      const semName = sem.semester || sem.name || null;
      const activities = Array.isArray(sem.activities) ? sem.activities : [];

      activities.forEach((act) => {
        // prefer startDate, fall back to date or a generic date field
        const rawDate = act.startDate ?? act.date ?? act.start ?? null;
        const actDate = rawDate ? new Date(rawDate) : null;
        if (!actDate || isNaN(actDate)) return;

        const diff = Math.abs(actDate - today);

        if (!closest || diff < closest.diff) {
          closest = { diff, semester: semName, activity: act, date: actDate };
        }
      });
    });

    return closest ? closest.semester : null;
  }
  
  autoScroll(element){
    const items = element.querySelectorAll('.timeline-item');
    const today = new Date().toISOString().split('T')[0];

      let targetItem = null;
      for (const item of items) {
        if (item.dataset.date >= today) {
          targetItem = item;
          break;
        }
      }

      // If no future events, go to the most recent past event
      if (!targetItem && items.length > 0) {
        targetItem = items[items.length - 1];
      }

      if (targetItem) {
        // Scroll into view with offset
        targetItem.scrollIntoView({ behavior: 'smooth', block: 'center' });
        
        // Add highlight animation
        const todayIndicator = targetItem.querySelector('.text-green-600');
        if (todayIndicator) {
          todayIndicator.classList.add('animate-pulse-once');
        }
      }
  }

  renderHTML(semester) {
    semester = semester || this.semester();
    if (!semester) return `<p class="text-gray-600 dark:text-gray-400">No upcoming activities found.</p>`;
    
    const semData = this.data.find((sem) => sem.semester === semester);
    if (!semData) return `<p class="text-gray-600 dark:text-gray-400">No data found for semester: ${semester}</p>`;
    
    const today = new Date();

    // Find the next exam activity
    //let nextExamIdx = -1;
    let nextExamDate = null;
    for (let i = 0; i < semData.activities.length; i++) {
      const act = semData.activities[i];
      const rawDate = act.startDate ?? act.date ?? act.start ?? null;
      const actDate = rawDate ? new Date(rawDate) : null;
      if (act.type === 'exams' && actDate && actDate >= today) {
        //nextExamIdx = i;
        nextExamDate = actDate;
        break;
      }
    }
    
    // Calculate days until the next exam
    let infoDiv = '';
    if (nextExamDate) {
      // Calculate difference in days (rounded up)
      const msPerDay = 1000 * 60 * 60 * 24;
      const diffMs = nextExamDate.setHours(0,0,0,0) - today.setHours(0,0,0,0);
      const daysToExam = Math.ceil(diffMs / msPerDay);
      if (daysToExam > 1) {
        let session = `${today.getFullYear()}/${today.getFullYear() +1 - 2000}`;
        infoDiv = `
        <div class=\"mb-4 text-transparent bg-gradient-to-r from-red-400 via-red-500 to-red-600 [background-clip:text] [-webkit-background-clip:text] font-medium\">    
            ${daysToExam} Days to ${session} ${semester} Exams
          </div>
        </div>`;
      } else if (daysToExam === 1) {
        infoDiv = `<div class=\"mb-4 text-transparent bg-gradient-to-r from-red-400 via-red-500 to-red-600 [background-clip:text] [-webkit-background-clip:text] font-medium\">${session} ${semester} Exams Start Tomorrow (${nextExamDate.toLocaleDateString(undefined, { year: 'numeric', month: 'long', day: 'numeric' })})</div>`;
      } else if (daysToExam === 0) {
        infoDiv = `<div class=\"mb-4 text-transparent bg-gradient-to-r from-red-400 via-red-500 to-red-600 [background-clip:text] [-webkit-background-clip:text] font-medium\">${session} ${semester} exams start today! <!--(${nextExamDate.toLocaleDateString(undefined, { year: 'numeric', month: 'long', day: 'numeric' })})--></div>`;
      } else {
        infoDiv = `<div class=\"mb-4 text-transparent bg-gradient-to-r from-red-400 via-red-500 to-red-600 [background-clip:text] [-webkit-background-clip:text] font-medium\">No upcoming exams in this semester.</div>`;
      }
    } else {
      infoDiv = `<div class=\"mb-4 text-transparent bg-gradient-to-r from-red-400 via-red-500 to-red-600 [background-clip:text] [-webkit-background-clip:text] font-medium\">No upcoming exams in this semester.</div>`;
    }
    
    let timeLineHTML = ``;
    

    semData.activities.forEach((act) => {
      const rawDate = act.startDate ?? act.date ?? act.start ?? null;
      const actDate = rawDate ? new Date(rawDate) : null;
      if (!actDate) return;

      const formattedDate = actDate.toLocaleDateString(undefined, {
        year: "numeric",
        month: "long",
        day: "numeric"
      });

      const isToday = actDate.toDateString() === today.toDateString();
      const isPast = actDate < today;
      const isFuture = actDate > today;

      // Style variations based on date relationship to today
      const dotClass = isToday ? 
        "w-4 h-4 bg-green-500 rounded-full ring-4 ring-green-200 dark:ring-green-900" :
        isPast ? 
          "w-3 h-3 bg-gray-400 rounded-full dark:bg-gray-500" :
          "w-3 h-3 bg-green-600 rounded-full";

      timeLineHTML += `
        <div  class="calendar-container timeline-item flex gap-4"   data-date="${actDate.toISOString().split('T')[0]}">
          <span class="flex flex-col items-center">
            <div class="${dotClass}"></div>
            <div class="w-px h-full bg-gray-300 dark:bg-gray-600 mt-2"></div>
          </span>
          <div class="flex-1 pb-8">
            <div class="flex items-center gap-2">
              <time class="text-sm font-medium ${isToday ? 'text-green-600 dark:text-green-400 font-bold' : 'text-gray-600 dark:text-gray-400'}">${formattedDate}</time>
              ${isToday ? '<span class="px-2 py-0.5 text-xs font-semibold text-white bg-green-600 rounded-full">TODAY</span>' : ''}
            </div>
            <h4 class="text-lg font-semibold text-gray-900 dark:text-white mt-1 ${isToday ? 'text-xl font-bold' : ''}">${act.description}</h4>
            ${act.type ? `<span class="mt-1 inline-block px-2 py-1 text-xs font-medium rounded ${
              act.type === 'lectures' ? 'bg-blue-100 text-blue-700 dark:bg-blue-900 dark:text-blue-300' :
              act.type === 'exams' ? 'bg-red-100 text-red-700 dark:bg-red-900 dark:text-red-300' :
              act.type === 'break' ? 'bg-yellow-100 text-yellow-700 dark:bg-yellow-900 dark:text-yellow-300' :
              act.type === 'registration' ? 'bg-purple-100 text-purple-700 dark:bg-purple-900 dark:text-purple-300' :
              'bg-gray-100 text-gray-700 dark:bg-gray-800 dark:text-gray-300'
            }">${act.type}</span>` : ''}
            ${act.week ? `<p class="text-sm text-gray-500 dark:text-gray-400 mt-1">Week ${act.week}</p>` : ''}
            ${act.type === 'ceremony' ? `<br/><a href="./map.html" class="mt-1 px-2 py-1 text-xs font-medium rounded dark:text-gray-950  text-gray-50 inline-flex items-center justify-center bg-green-700 hover:bg-green-800 focus:ring-4 focus:outline-none  dark:bg-green-600 dark:hover:bg-green-700 ">
              <svg class="w-4 h-4 me-2" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 24 24">
                <path fill-rule="evenodd" d="M11.906 1.994a8.002 8.002 0 0 1 8.09 8.421 7.996 7.996 0 0 1-1.297 3.957.996.996 0 0 1-.133.204l-.108.129c-.178.243-.37.477-.573.699l-5.112 6.224a1 1 0 0 1-1.545 0L5.982 15.26l-.002-.002a18.146 18.146 0 0 1-.309-.38l-.133-.163a.999.999 0 0 1-.13-.202 7.995 7.995 0 0 1 6.498-12.518ZM15 9.997a3 3 0 1 1-5.999 0 3 3 0 0 1 5.999 0Z" clip-rule="evenodd"/>
              </svg> ${act.description.match(/(\w+)\s+ceremony/i)[1]} Arena</a>` : ''}
          </div>
        </div>
      `;
    });

    // Add animation styles if they don't exist
    if (!document.getElementById('timeline-animations')) {
      const style = document.createElement('style');
      style.id = 'timeline-animations';
      style.textContent = `
        @keyframes pulse-once {
          0%, 100% { box-shadow: 0 0 0 0 rgba(34, 197, 94, 0.4); }
          50% { box-shadow: 0 0 0 12px rgba(34, 197, 94, 0); }
        }
        .animate-pulse-once {
          animation: pulse-once 1.2s ease-out;
        }
      `;
      document.head.appendChild(style);
    }

    let contentHTML = `
      <div class=" mx-auto">
        <h3 class="text-2xl font-bold mb-2 text-gray-900 dark:text-white">Upcoming ${semester} Activities</h3>
        <div class="inline-flex">
          ${infoDiv} 
          
        </div>
        <!-- Scrollable timeline container -->
        <div id="timelineContainer" class="h-92 overflow-y-auto border border-gray-200/15 backdrop-blur-sm rounded-xl shadow-sm bg-white/75 dark:bg-green-900/5 p-4 space-y-6">
          ${timeLineHTML}
        </div>
      </div>
    `;

    
    setTimeout(() => {
      
      const timelineContainer = document.getElementById('timelineContainer');
      if (!timelineContainer) return;
      
      timelineContainer.scrollIntoView({ behavior: 'smooth', block: 'start' });

      // Then, after a short delay to allow the page scroll to complete,
      // scroll the container to the relevant timeline item
      setTimeout(() => {
        const items = timelineContainer.querySelectorAll('.timeline-item');
        const today = new Date().toISOString().split('T')[0];

        let targetItem = null;
        for (const item of items) {
          if (item.dataset.date >= today) {
            targetItem = item;
            break;
          }
        }

        // If no future events, go to the most recent past event
        if (!targetItem && items.length > 0) {
          targetItem = items[items.length - 1];
        }

        if (targetItem) {
          // Calculate scroll position to center the target item
          const containerRect = timelineContainer.getBoundingClientRect();
          const itemRect = targetItem.getBoundingClientRect();
          const scrollTop = timelineContainer.scrollTop + (itemRect.top - containerRect.top) - (containerRect.height - itemRect.height) / 2;
          
          // Smooth scroll within the container
          timelineContainer.scrollTo({
            top: scrollTop,
            behavior: 'smooth'
          });
          
          // Add highlight animation
          const todayIndicator = targetItem.querySelector('.text-green-600');
          if (todayIndicator) {
            todayIndicator.classList.add('animate-pulse-once');
          }
        }
      }, 500); // Wait for page scroll to complete
    }, 100);
    

    // Add auto-scroll functionality
    setTimeout((container) => {
      container = document.getElementById('timelineContainer');
      this.autoScroll(container)
    }, 100);

    return contentHTML;
  }
}

const calendarData = new CalendarData("calendar-data")

export { mapResources, timetable, armsOfGov, calendar, mapData, timetablesData, calendarData };
